   Interfaces
   ================================================= */

interface Chapter {
  id: string;
  label: string;
  active: boolean;
}

interface PanelConfig {
  id: string;
  title: string;
  expanded: boolean;
  type: 'empty' | 'radio';
  description?: string;
  selectedValue?: string;
  options?: RadioOption[];
  previewText?: string;
}

interface RadioOption {
  value: string;
  label: string;
}

